package uz.fozilbek.lesson23.coreimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.BaseAdapterimport kotlinx.coroutines.flow.combineimport uz.fozilbek.lesson23.core.models.CustomerDataimport uz.fozilbek.lesson23.databinding.ItemCustomerBindingclass CustomerAdapter : BaseAdapter() {    var onItemDelete: ((CustomerData) -> Unit)? = null    var onItemEdit: ((CustomerData) -> Unit)? = null    private val data = ArrayList<CustomerData>()    fun setData(d: ArrayList<CustomerData>) {        this.data.clear()        this.data.addAll(d)        notifyDataSetChanged()    }    override fun getCount(): Int {        return data.size    }    override fun getItem(position: Int): CustomerData {        return data[position]    }    override fun getItemId(position: Int): Long {        return getItem(position).id    }    override fun getView(position: Int, convertView: View?, parent: ViewGroup): View {        val view = ItemCustomerBinding.inflate(LayoutInflater.from(parent.context), parent, false)        val d = getItem(position)        view.nameCustomer.text = d.name        view.numberCustomer.text = d.phoneNumber        view.ageCustomer.text = "Age: ".plus(d.age)        view.cityCustomer.text = "City: ".plus(d.city)        view.editItem.setOnClickListener {            onItemEdit?.invoke(d)        }        view.deleteItem.setOnClickListener {            onItemDelete?.invoke(d)        }        return view.root    }}